<?phpob_start();define('SZINT', 666);require_once('rendszer/mag.php');$belep = new belep();$old = new old();$oldal_query = array('gyik', 'szab', 'link', 'jogi');if(in_array($g['mit'], $oldal_query) === true) {		switch($g['mit']) {			case 'jogi':				$template = "dokument.tpl";				db::futat("SELECT cid, cim, text FROM cikk WHERE mihez = 'jogi' ORDER BY suly, cim");				$t = db::elso_sor();				$kesz['cim'] = $t['cim'];				$kesz['cid'] = $t['cid'];				$kesz['text'] = explode('##kep##', bb::bbdecode($t['text']));								$smarty->assign('modul', 'jogi');				$smarty->assign('lista', $kesz);			break;			default:				db::futat("SELECT cid, cim, text, mod_date AS datum, name FROM cikk c LEFT JOIN users u ON c.mod_user = u.uid WHERE mihez = '%s' ORDER BY suly, cim", $g['mit']);				$tomb = db::tomb();				foreach($tomb as $key => $val) {					$tomb[$key]['text'] = bb::bbdecode($val['text']);				}				$smarty->assign('lista', $tomb);			break;		}		$smarty->assign('fejlec_elotag', $DOKUMNETUM_TIPUSOK[$g['mit']]);} elseif(!empty($g['id']) && is_numeric($g['id'])) {	$doc = new Doc();	$id = (int)$g['id'];	$data = $doc->getById($g['id']);	$data['text'] = bb::bbdecode($data['text']);	$smarty->assign('lista', array($data));} else {	$OLDAL[] = hiba_uzi('Érvénytelen oldalhívás');}$smarty->assign('OLDAL', $OLDAL);$smarty->display(!empty($template) ? $template : 'dokumentacio.tpl');ob_end_flush();?>